select crd.`operator_id` as `employee_id`, em.`name` as `employee_name`, pro.`integral` as `integral` from consume_record_detail crd left join employee em on crd.`operator_id`=em.`id` left join project pro on crd.`project_id`= pro.`id` where crd.`operator_id` =#{employeeId,jdbcType=BIGINT} and
AND #{beginTime,jdbcType=TIMESTAMP}<= crd.`create_time`
AND consume_time <= #{endTime,jdbcType=TIMESTAMP}

