mapper.select:
		SELECT
		*
		from
		consume_record
		
			
				`store_id` = #{storeId, jdbcType=BIGINT}
			
			
				AND `consume_type` = #{consumeType, jdbcType=INTEGER}
			
			
				AND #{beginTime,jdbcType=TIMESTAMP}<= consume_time <= #{endTime,jdbcType=TIMESTAMP} 
			
			
				AND  consume_time >= #{beginTime,jdbcType=TIMESTAMP}  
			
			
				AND  consume_time <= #{endTime,jdbcType=TIMESTAMP}  
			
			
				
				CONCAT(`trade_serial_number`,`customer_mobile`,`remark`) LIKE
				#{condition, jdbcType=VARCHAR}

		SELECT
		*
		from
		consume_record
		
			
				`store_id` = #{storeId, jdbcType=BIGINT}
			
			
				AND `consume_type` = #{consumeType, jdbcType=INTEGER}
			
			
				AND #{beginTime,jdbcType=TIMESTAMP}<= consume_time <= #{endTime,jdbcType=TIMESTAMP} 
			
			
				AND  consume_time >= #{beginTime,jdbcType=TIMESTAMP}  
			
			
				AND  consume_time <= #{endTime,jdbcType=TIMESTAMP}  
			
			
				
				CONCAT(`trade_serial_number`,`customer_mobile`,`remark`) LIKE
				#{condition, jdbcType=VARCHAR}
			
			and is_modified = 1

mapper.update:
update consume_record set is_modified = 1
where
id = #{id,jdbcType=BIGINT}
