SELECT u.`id` AS user_id, u.`role` AS u_role, u.`account` AS user_account, u.`email` AS user_email, u.`mobile` AS user_mobile, u.`name` AS user_name, u.`gender` AS user_gender, u.`store_id` AS user_store_id, u.`remark` AS user_remark, u.`created_time` AS user_created_time, u.`modified_time` AS user_modified_time, s.`name` AS store_name, ut.`token` AS user_token_token FROM user u LEFT JOIN store s ON u.`store_id` = s.`id` LEFT JOIN user_token ut ON u.`id` = ut.`user_id`
